const writeProperty = JSON.parse('{"deviceIPAddress":"' + device.controller.ipAddress + '", "objectId": { "type":' + bacnetObject.objectType + ', "instance":' + bacnetObject.instanceNum + '}, "propertyId": 85, "values": [ { "type":' + ((bacnetObject.objectType==2)?4:0) +', "value":' + bacnetObject.value +  '} ]}');

