                if (deviceList[device].bacnet.objects[object].instanceNum >= deviceList[device].bacnet.instanceRangeBV){
                node.error(`For device "${device}" : instanceNum too high for "${object}"`);
                node.status({ fill: "red", shape: "dot", text: object + " instanceNum too high" });
                return null;
                }
                instanceRangeBV++;
                break;
            default:

        }
        if(deviceList[device].bacnet.objects[object].instanceNum < 0){
            node.error(`For device "${device}" : Negative instanceNum for "${object}"`);
            node.status({ fill: "red", shape: "dot", text: object + " negative instanceNum" });
            return null;
        }
    }
    if (deviceList[device].bacnet.instanceRangeAV < instanceRangeAV) {
        node.error(`For device "${device}" : instanceRange too small for Analog Value`);
        node.status({ fill: "red", shape: "dot", text: "instanceRange too small" });
        return null;
    }
    if (deviceList[device].bacnet.instanceRangeBV < instanceRangeBV) {
        node.error(`For device "${device}" : instanceRange too small for Binary Value`);
        node.status({ fill: "red", shape: "dot", text: "instanceRange too small" });
        return null;
    }

    // Build an array with offsetAV, instanceRangeAV and device
    objectInstanceArrayAV.push({ "device" : device, "offset" : deviceList[device].bacnet.offsetAV, "instanceRange" : deviceList[device].bacnet.instanceRangeAV});
    // Build another array with offsetAV, instanceRangeAV and device
    objectInstanceArrayBV.push({ "device" : device, "offset" : deviceList[device].bacnet.offsetBV, "instanceRange" : deviceList[device].bacnet.instanceRangeBV});
}

// Check that offset and instanceRange don't overlap and calcul maxDeviceNum for each type.
objectInstanceArrayAV.sort((a, b) => a.offset - b.offset);
objectInstanceArrayBV.sort((a, b) => a.offset - b.offset);
for (let i = 0; i < objectInstanceArrayAV.length - 1; i++) {
    if (objectInstanceArrayAV[i].instanceRange != 0){
        if (objectInstanceArrayAV[i].offset + objectInstanceArrayAV[i].instanceRange > objectInstanceArrayAV[i + 1].offset) {
            node.error(`BACnet objects of Device "${objectInstanceArrayAV[i].device}" and "${objectInstanceArrayAV[i + 1].device}" overlap`)
            node.status({ fill: "red", shape: "dot", text: "BACnet objects instances overlap" });
            return null;
        }
        else{
            maxDevNumAV = Math.trunc((objectInstanceArrayAV[i + 1].offset - objectInstanceArrayAV[i].offset) / objectInstanceArrayAV[i].instanceRange) - 1;
        }
    }else{
        maxDevNumAV = 0
    }
    if (objectInstanceArrayBV[i].instanceRange != 0){
        if (objectInstanceArrayBV[i].offset + objectInstanceArrayBV[i].instanceRange > objectInstanceArrayBV[i + 1].offset) {
            node.error(`BACnet objects of Device "${objectInstanceArrayBV[i].device}" and "${objectInstanceArrayBV[i + 1].device}" overlap`)
            node.status({ fill: "red", shape: "dot", text: "BACnet objects instances overlap" });
            return null;
        }
        else{
            maxDevNumBV = Math.trunc((objectInstanceArrayBV[i + 1].offset - objectInstanceArrayBV[i].offset) / objectInstanceArrayBV[i].instanceRange) - 1;
        }
    } else {
        maxDevNumBV = 0
    }

    if (maxDevNumBV != 0 && maxDevNumAV != 0){
        deviceList[objectInstanceArrayAV[i].device].identity.maxDeviceNum = Math.min(maxDevNumAV,maxDevNumBV);
    }else{
        deviceList[objectInstanceArrayAV[i].device].identity.maxDeviceNum = Math.max(maxDevNumAV, maxDevNumBV);
    }
}

////////////////////////////////////
// Return if all checkings are clear
///////////////////////////////////
node.status({ fill: "green", shape: "dot", text: "Configuration OK" });

return {
    payload: deviceList
}

