// 1. Flush the downlink Queue

if (device.lorawan.networkServer == "chirpstack") {
    if (device.lorawan.flushDownlinkQueue == true) {

        //We include flow value from libraries
        var grpc = flow.get('grpc');
        var device_grpc = flow.get('device_grpc');
        var device_pb = flow.get('device_pb');

        // This must point to the ChirpStack API interface.
        const server = "chirpstack.univ-lorawan.fr:9090";
        // The DevEUI for which we want to enqueue the downlink.
        const devEui = device.identity.devEUI;

        // The API token (can be obtained through the ChirpStack web-interface).
        const apiToken = device.lorawan.chirpstack.grpcApikey;

        // Create the client for the DeviceService.
        const deviceService = new device_grpc.DeviceServiceClient(
            server,
            grpc.credentials.createInsecure(),
        );

        // Create the Metadata object.
        const metadata = new grpc.Metadata();
        metadata.set("authorization", "Bearer " + apiToken);

        //Flush downlink queue request
        const flushReq = new device_pb.FlushDeviceQueueRequest();
        flushReq.setDevEui(devEui);

        //Send the request
        deviceService.flushQueue(flushReq, metadata, (err, resp) => {
            if (err !== null) {
                //node.error(`Can't flush ChirpStack downlink queue :  ${err}`);
            }
        });
    }

    // 2. Prepare downlink JSON
    downlinkJson = {
        "topic": device.mqtt.topicDownlink,
        "payload": {
            "devEui": device.identity.devEUI,
            "confirmed": false,
            "fPort": device.lorawan.downlinkPort,
            "object": msg.payload
        }
    }
    debug(device, "txTime", `${device.identity.deviceName} (${device.controller.protocol}) : TX time = ${Date.now() - device.transmitTime} ms`); 
    return downlinkJson;
}


