/////////////////////////////////////////////////////////////////////////
if (device.lorawan.networkServer == "actility") {
    downlinkJson = {
        "topic": device.mqtt.topicDownlink,
        "payload": {
            "DevEUI_downlink": {
                "DevEUI": device.identity.devEUI,
                "FPort": device.lorawan.downlinkPort,
                "payload": msg.payload,
                "FlushDownlinkQueue": String(+device.lorawan.flushDownlinkQueue),
                "DriverCfg": {
