                  "value" : "xx"
                },
                ...
            ]
        },
        "requestTimeout" : xxx (ms)
    }
    */

    let property_references = [];
    for (let object in bacnetObjects) {
      if (bacnetObjects[object].dataDirection == dataDirection) {
        let temp = {}
        switch (dataDirection) {
          case "uplink":
            temp = '{ "type": "' + bacnetObjects[object].objectType + '", "instance": ' + bacnetObjects[object].instanceNum + ', "property": "presentValue", "value": ' + bacnetObjects[object].value + ' }';
            property_references.push(JSON.parse(temp));
            break;
          case "downlink":
            temp = '{ "type": "' + bacnetObjects[object].objectType + '", "instance": ' + bacnetObjects[object].instanceNum + ', "property": "presentValue"}';
            property_references.push(JSON.parse(temp));
            break;
          default:

        }
      }
    }

    // Return HTTP Request
    switch (dataDirection) {
      case "uplink":
        return {
          "method": "POST",
          "url": "https://" + device.controller.ipAddress + "/api/rest/v2/services/bacnet/local/objects/write-property-multiple",
          "headers": {
            Authorization: device.controller.httpAuthentication,
            ContentType: "application/json"
          },
          "payload": {
            "encode": "text",
            "property-references": property_references
          },
          "requestTimeout": flow.get('$parent.g_httpRequestTimeOut'),
          "device": device
        }
        break;
      case "downlink":
        return {
          "method": "POST",
          "url": "https://" + device.controller.ipAddress + "/api/rest/v2/services/bacnet/local/objects/read-property-multiple",
          "headers": {
            Authorization: device.controller.httpAuthentication,
            ContentType: "application/json"
          },
          "payload": {
            "encode": "text",
            "property-references": property_references
          },
          "requestTimeout": flow.get('$parent.g_httpRequestTimeOut'),
          "device": device
        }
        break;
      default:
      return
        break;
    }



  ///////////////////////////////////////////////////////////
  ////// XXXXX Controller
  ////// URL to the API documentation
  ///////////////////////////////////////////////////////////
  case "anotherController":

    break;
  default:

    return null;
    break;
}

