let client = new nodeBacnet();
let device = msg.device;
let debug = flow.get("g_debug");
let temp;

// If the device controller protocol is not "bacnet" there is no need to be here 
if(device?.controller?.protocol != "bacnet") return null;

let bacnetObject = device.bacnet.objects;

for (let object in bacnetObject) {
  if (bacnetObject[object].dataDirection === "uplink") {
    //  Read of the uplink bacnet objects
    client.writeProperty(device.controller.ipAddress, {type: bacnetObject[object].objectType, instance: bacnetObject[object].instanceNum}, 85, [{type: ((bacnetObject[object].objectType == 2) ? 4 : 0), value: bacnetObject[object].value}], (err, value) => {
      if(err){
          msg.payload = err;
          node.status({fill:"red",shape:"dot",text:"BACnet "+ err});
          node.error("Error writing bacnet objects", {
            errorType: "nativeBACnet",
            error: err,

          });
          return null;
        } else {
          msg.payload = value;
          debug(device, "up",`${device.identity.deviceName} (${device.controller.protocol}) : Write Uplink Objects`)
          node.status({fill:"green",shape:"dot",text:"Native BACnet"});
        }
    });

  }
}
return msg;
